INSERT INTO `question` (`id`,`category`,`content`,`difficulty`,`reference`) VALUES (34,'Historia Polski','W którym roku miał miejsce I rozbiór Polski?',4,'Podpisanie traktatu rozbiorowego nastąpiło 5 sierpnia 1772 roku w Petersburgu. Rozbioru i podziału ziem terytorium I Rzeczypospolitej dokonały Prusy, Imperium Habsburgów i Imperium Rosyjskie. Kolejne rozbiory miały miejsca w latach 1793 i 1795.');
