INSERT INTO `question` (`id`,`category`,`content`,`difficulty`,`reference`) VALUES (37,'Historia Polski','Który polski dywizjon lotniczy zestrzelił podczas Bitwy o Anglię najwięcej maszyn Luftwaffe?',1,'Dywizjon 303 był najlepszą jednostką lotniczą, biorącą udział w bitwie o Anglię - zgłosił zestrzelenie 126 maszyn Luftwaffe.');
