INSERT INTO `question` (`id`,`category`,`content`,`difficulty`,`reference`) VALUES (52,'Historia Polski','Kto jest autorem melodii \"Mazurka Dąbrowskiego\"?',2,'Początkowo uważano, że autorem melodii jest Michał Kleofas Ogiński, jednak materiały archiwalne temu zaprzeczyły. Dziś autorzy śpiewników i prac naukowych najczęściej podają określenie \"melodia ludowa\".');
