INSERT INTO `question` (`id`,`category`,`content`,`difficulty`,`reference`) VALUES (61,'Historia Polski','Gdzie mieści się Muzeum Hymnu Narodowego?',3,'Muzeum Hymnu Narodowego mieści się w Będominie. Dokładnie w dworze szlacheckim, który w latach 1740-1785 należał do rodziny Wybickich i właśnie w nim urodził się Józef Wybicki.');
INSERT INTO `question` (`id`,`category`,`content`,`difficulty`,`reference`) VALUES (62,'Historia Polski','Potop Szwedzki rozpoczął się w roku...',3,'Najazd Szwecji na Rzeczpospolitą, czyli Potop Szwedzki, rozpoczął się w 1655 roku, w czasie II wojny północnej (1655–1660). Zakończył go pokój w Oliwie zawarty w 1660 roku.');
